    const [message, setMessage] = useState("");
    const [messages, setMessages] =useState([]);



    useEffect(() => {
        socket.on('CHAT', message => {
            setMessages([...messages, message]);
        })


        return () => {socket.off()}
    }, [messages]);


    const submit = (e) => {
        e.preventDefault();
        socket.emit('CHAT', {name, message});
        setMessage(""); 
    };

    return (
        <div className="flights">
            <h3>Vuelos</h3>

            <div className="map">

            </div>


        </div>


    );
};

