import React, {useState, useEffect , useRef} from 'react';
//import Socket from './Socket';
import socket from './Socket';
import '../App.css';



const Flights = ({ name }) => {

    const [flights_list, setFlights] = useState([]);



    useEffect(() => {
        socket.on('FLIGHTS', flight_list => {
            setFlights(flight_list);
        });


        //return () => {socket.off()}
    }, [flights_list]);

    const divRef = useRef(null);
    useEffect(() =>{
        divRef.current.scrollIntoView({behavior: 'smooth'});
    });

    return (
        <div className="flights">
            <h3>Vuelos</h3>

            <div className="map">

            </div>

            <div className="flightsList">
                {flights_list.map((e,i)=> <div key={i}> <div>{e.code}</div><div>{e.airline}</div> </div>)}
                <div ref={divRef}></div>
            </div>


        </div>


    );
};

export default Flights
